import React, { useEffect, useState, useRef } from "react";

const webSocketUrl = process.env.REACT_APP_API;

export default function ChatWindow() {
  const [chatData, setChatData] = useState([]);
  const [chat, setChat] = useState("");
  const inputRef = useRef(null);
  const scrollRef = useRef(null);

  const submitChat = (e) => {
    let chatMessage = e.target.value;
    if (e.key === "Enter") {
      inputRef.current.value = "";
      inputRef.current.focus();
      setChat(chatMessage);
      const message = {
        user: "Me",
        message: chatMessage,
      };
      console.log(`submitChat called! ${JSON.stringify(message)}`);
      setChatData((arr) => [...arr, message]);
      setChat("");
    }
  };
  useEffect(() => {
    let webSocket;
    if (webSocketUrl) {
      webSocket = new WebSocket(webSocketUrl);

      // webSocket.addEventListener("open", function (event) {
      //   webSocket.send("Hello from React!");
      // });

      webSocket.addEventListener("message", function (event) {
        const data = JSON.parse(event.data);
        setChatData((arr) => [...arr, data]);
        scrollRef.current.scrollIntoView({ behavior: "smooth" });
      });
    }

    return () => {
      if (webSocketUrl !== undefined) {
        webSocket.close();
      }
    };
  }, [chat]);

  useEffect(() => {
    scrollRef.current.scrollIntoView({ behavior: "smooth" });
  }, [chatData]);

  return (
    <div
      style={{
        minHeight: "100%",
        maxHeight: "100%",
        display: "flex",
        flexDirection: "column",
        // justifyItems: "flex-end",
        // border: "5px #89c2d9",
        borderRadius: "10px",
        backgroundColor: "#a9d6e5",
      }}
    >
      <div
        style={{
          display: "grid",
          alignContent: "center",
          justifyContent: "center",
        }}
      >
        <h3>Adithya's Chat</h3>
      </div>
      <div
        style={{
          flex: "9.75",
          overflow: "auto",
          backgroundColor: "#012a4a",
          border: "1px #89c2d9",
          borderRadius: "10px",
        }}
      >
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            backgroundColor: "#232132",
          }}
        >
          <div
            style={{
              minHeight: "100vh",
            }}
          />
          {chatData.map((item) => (
            <div
              key={item.message}
              style={{
                margin: "5px",
              }}
            >
              <span className="userStyle">{item.user}</span>
              <span key={item.user} className="messageStyle">
                {item.message}
              </span>
            </div>
          ))}
          <div ref={scrollRef} />
        </div>
      </div>
      <div className="chat">
        <input
          type="text"
          placeholder="Type Message"
          className="chatBar"
          ref={inputRef}
          onKeyDown={submitChat}
        />
      </div>
    </div>
  );
}
